import xlrd
def excle_to_list(file,tag):
    data_list = []
    WorkBook = xlrd.open_workbook(file)     #打开要读取数据的Excel文件
    Sheet = WorkBook.sheet_by_name(tag)     #获取要读取得sheet工作表
    rows = Sheet.nrows      #获取工作表中数据的总行数
    header = Sheet.row_values(0)    #获取工作表中第一行的数据
    for i in range(1,rows): #读取除第一行以外的其他行数据
        data_every_row = Sheet.row_values(i)    #读取每一行的数据
        data = dict(zip(header,data_every_row)) #使用zip()函数把获取到的第一行数据与其他行数据分别组合成元组，再使用dict()方法将其组合成字典
        data_list.append(data)  #将组合后的数据追加到data_list列表
    return data_list

